import React, { useContext } from 'react';
import axios from 'axios';
import { CartContext } from '../components/CartContext';
import Navbar from '../components/Navbar';
import './CartPage.css';
import { FaTrashAlt } from 'react-icons/fa'; // Importa el ícono de tachar roja

const CartPage = () => {
    const { cart, total, clearCart, updateQuantity, removeFromCart } = useContext(CartContext);

    const handlePlaceOrder = async () => {
        try {
            const token = localStorage.getItem('token');
            const response = await axios.post(
                'http://localhost:5000/api/orders',
                { products: cart, total, estado: 'Pendiente' },
                {
                    headers: {
                        Authorization: `Bearer ${token}`
                    }
                }
            );
            console.log('Order placed:', response.data);
            clearCart();
        } catch (error) {
            console.error('Error placing order:', error);
        }
    };

    return (
        <div>
            <Navbar />
            <h1 className="cart-title">Shopping Cart</h1>
            <div className="cart-container">
                {cart.length > 0 ? (
                    <>
                        <table className="cart-table">
                            <thead>
                                <tr>
                                    <th></th>
                                    <th>Product</th>
                                    <th>Price</th>
                                    <th>Quantity</th>
                                    <th>Subtotal</th>
                                </tr>
                            </thead>
                            <tbody>
                                {cart.map((item, index) => (
                                    <tr key={index}>
                                        <td>
                                            <FaTrashAlt 
                                                className="remove-icon" 
                                                onClick={() => removeFromCart(index)} 
                                            />
                                        </td>
                                        <td className="product-info">
                                            <img src={item.Imagen} alt={item.Nombre} className="product-image" />
                                            <span>{item.Nombre}</span>
                                        </td>
                                        <td>${item.Precio}</td>
                                        <td className="quantity-control">
                                            <button onClick={() => updateQuantity(index, item.Cantidad - 1)} disabled={item.Cantidad <= 1}>-</button>
                                            <span>{item.Cantidad}</span>
                                            <button onClick={() => updateQuantity(index, item.Cantidad + 1)}>+</button>
                                        </td>
                                        <td>${item.Precio * item.Cantidad}</td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                        <div className="cart-summary">
                            <div className="promo-code">
                                <input type="text" placeholder="Coupon code" />
                                <button className="apply-button">APPLY COUPON</button>
                            </div>
                            <div className="totals">
                                <div className="subtotal">
                                    <span>Subtotal:</span>
                                    <span>${total}</span>
                                </div>
                                <div className="total">
                                    <span>Total:</span>
                                    <span>${total}</span>
                                </div>
                            </div>
                        </div>
                        <div className="cart-actions">
                            <button className="cart-button red-button" onClick={clearCart}>Vaciar Carrito</button>
                            <button className="cart-button green-button" onClick={handlePlaceOrder}>Proceed to Checkout</button>
                        </div>
                    </>
                ) : (
                    <p>Tu carrito está vacío.</p>
                )}
            </div>
        </div>
    );
};

export default CartPage;
