import React, { useEffect, useState, useContext } from 'react';
import axios from 'axios';
import Navbar from '../components/Navbar';
import { CartContext } from '../components/CartContext';
import './ProductPage.css';
import { Link } from 'react-router-dom';

const ProductsPage = () => {
    const [products, setProducts] = useState([]);
    const [showModal, setShowModal] = useState(false); // Estado para el modal
    const { addToCart } = useContext(CartContext);

    useEffect(() => {
        const fetchProducts = async () => {
            try {
                const response = await axios.get('http://localhost:5000/api/products');
                console.log('Products:', response.data);
                setProducts(response.data);
            } catch (error) {
                console.error('Error fetching products:', error);
            }
        };
        fetchProducts();
    }, []);

    const handleAddToCart = (product) => {
        addToCart(product);
        setShowModal(true);
        setTimeout(() => {
            setShowModal(false);
        }, 3000); // Oculta el modal después de 3 segundos
    };

    return (
        <div>
            <Navbar />
            <div className="product-page">
                <div className="banner">
                    <h1>Nuevo producto, compralo yayaya</h1>
                    <Link to="/products">
                        <button className="banner-button">Compralo ahora</button>
                    </Link>
                </div>
                <div className="filters">
                    {/* Mantén solo dos botones de filtro aquí */}
                    <button>Gatitos</button>
                    <button>Perritos</button>
                </div>
                <div className="productos-container">
                    <h2>Los mejores productos para tus animales!</h2>
                    {showModal && <div className="confirmation-modal">El producto se ha agregado correctamente al carrito</div>}
                    <div className="productos-grid">
                        {products.map((product) => (
                            <div className="product-card" key={product.ProductoID}>
                                <img src={product.Imagen} alt={product.Nombre} />
                                <div className="product-info">
                                    <div className="product-header">
                                        <h3>{product.Nombre}</h3>
                                        <p className="product-price">${product.Precio}</p>
                                    </div>
                                    <p className="product-description">{product.Descripcion}</p>
                                    <p className="product-stock">Existencias: {product.Stock}</p>
                                    <button className="add-to-cart" onClick={() => handleAddToCart(product)}>Agregar al carrito</button>
                                </div>
                            </div>
                        ))}
                    </div>
                </div>
            </div>
        </div>
    );
};

export default ProductsPage;
