import React, { createContext, useState } from 'react';

export const CartContext = createContext();

export const CartProvider = ({ children }) => {
    const [cart, setCart] = useState([]);
    const [total, setTotal] = useState(0);

    const addToCart = (product) => {
        setCart([...cart, product]);
        setTotal(total + product.Precio * product.Cantidad);
    };

    const clearCart = () => {
        setCart([]);
        setTotal(0);
    };

    const updateQuantity = (index, quantity) => {
        const newCart = [...cart];
        const item = newCart[index];
        const difference = (quantity - item.Cantidad) * item.Precio;
        item.Cantidad = quantity;
        setCart(newCart);
        setTotal(total + difference);
    };

    const removeFromCart = (index) => {
        const newCart = [...cart];
        const item = newCart.splice(index, 1)[0];
        setCart(newCart);
        setTotal(total - item.Precio * item.Cantidad);
    };

    return (
        <CartContext.Provider value={{ cart, total, addToCart, clearCart, updateQuantity, removeFromCart }}>
            {children}
        </CartContext.Provider>
    );
};
