import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import './AdminPage.css';

const AdminPage = () => {
    const [products, setProducts] = useState([]);
    const [newProduct, setNewProduct] = useState({
        name: '',
        price: '',
        stock: '',
        description: '',
        image: ''
    });

    useEffect(() => {
        const fetchProducts = async () => {
            try {
                const res = await axios.get('http://localhost:5000/api/products');
                setProducts(res.data);
            } catch (error) {
                console.error('Error fetching products', error);
            }
        };

        fetchProducts();
    }, []);

    const handleAddProduct = async (e) => {
        e.preventDefault();
        try {
            const res = await axios.post('http://localhost:5000/api/products', {
                Nombre: newProduct.name,
                Precio: newProduct.price,
                Stock: newProduct.stock,
                Descripcion: newProduct.description,
                Imagen: newProduct.image
            });
            setProducts([...products, res.data]);
            setNewProduct({ name: '', price: '', stock: '', description: '', image: '' });
        } catch (error) {
            console.error('Error adding product', error);
        }
    };

    const handleDeleteProduct = async (productId) => {
        try {
            await axios.delete(`http://localhost:5000/api/products/${productId}`);
            setProducts(products.filter(product => product.ProductoID !== productId));
        } catch (error) {
            console.error('Error deleting product', error);
        }
    };

    return (
        <div className="admin-page">
            <div className="sidebar">
                <div className="sidebar-header">
                    <h2>PetStore</h2>
                </div>
                <ul className="sidebar-menu">
                    <li><Link to="/products">Productos</Link></li>
                    <li><Link to="/admin">Admin Panel</Link></li>
                    <li><Link to="/orders">Order Management</Link></li>
                    <li><Link to="/logout" className="logout-button">Cerrar Sesión</Link></li>
                </ul>
            </div>
            <div className="admin-content">
                <h2>Admin Panel</h2>
                <div className="admin-form-container">
                    <div className="add-product">
                        <h3>Añadir Producto</h3>
                        <form onSubmit={handleAddProduct}>
                            <input
                                type="text"
                                placeholder="Nombre"
                                value={newProduct.name}
                                onChange={(e) => setNewProduct({ ...newProduct, name: e.target.value })}
                            />
                            <input
                                type="number"
                                placeholder="Precio"
                                value={newProduct.price}
                                onChange={(e) => setNewProduct({ ...newProduct, price: e.target.value })}
                            />
                            <input
                                type="number"
                                placeholder="Stock"
                                value={newProduct.stock}
                                onChange={(e) => setNewProduct({ ...newProduct, stock: e.target.value })}
                            />
                            <textarea
                                placeholder="Descripción"
                                value={newProduct.description}
                                onChange={(e) => setNewProduct({ ...newProduct, description: e.target.value })}
                            ></textarea>
                            <input
                                type="text"
                                placeholder="URL de la Imagen"
                                value={newProduct.image}
                                onChange={(e) => setNewProduct({ ...newProduct, image: e.target.value })}
                            />
                            <button type="submit">Añadir Producto</button>
                        </form>
                    </div>
                    <div className="product-list">
                        <h3>Lista de Productos</h3>
                        <ul>
                            {products.map((product) => (
                                <li key={product.ProductoID}>
                                    <img src={product.Imagen} alt={product.Nombre} className="product-image" />
                                    <div className="product-info">
                                        <h4>{product.Nombre}</h4>
                                        <p>Precio: ${product.Precio}</p>
                                        <p>Stock: {product.Stock}</p>
                                        <p>Descripción: {product.Descripcion}</p>
                                    </div>
                                    <div className="product-actions">
                                        <button onClick={() => handleDeleteProduct(product.ProductoID)}>Eliminar</button>
                                    </div>
                                </li>
                            ))}
                        </ul>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default AdminPage;
