import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import './AdminOrdersPage.css'; // Asegúrate de crear y vincular este archivo CSS

const AdminOrdersPage = () => {
    const [orders, setOrders] = useState([]);

    useEffect(() => {
        const fetchOrders = async () => {
            try {
                const token = localStorage.getItem('token');
                const response = await axios.get('http://localhost:5000/api/admin/orders', {
                    headers: { Authorization: `Bearer ${token}` }
                });
                setOrders(response.data);
            } catch (error) {
                console.error('Error fetching orders:', error);
            }
        };

        fetchOrders();
    }, []);

    return (
        <div className="admin-orders-page">
            <div className="orders-sidebar">
                <div className="orders-sidebar-header">
                    <h2><Link to="/">PetStore</Link></h2>
                </div>
                <ul className="orders-sidebar-menu">
                    <li><Link to="/products">Productos</Link></li>
                    <li><Link to="/admin">Admin Panel</Link></li>
                    <li><Link to="/admin/orders">Order Management</Link></li>
                    <li><Link to="/logout" className="orders-logout-button">Cerrar Sesión</Link></li>
                </ul>
            </div>
            <div className="orders-admin-content">
                <h2>Order Management</h2>
                <div className="admin-orders-container">
                    {orders.map(order => (
                        <div key={order.PedidoID} className="order-card">
                            <div className="order-header">
                                <h3>ID pedido: <span className="highlight">{order.PedidoID}</span></h3>
                                <p>ID cliente: <span className="highlight">{order.ClienteID}</span></p>
                                <p>Fecha: {new Date(order.FechaPedido).toLocaleString()}</p>
                                <p>Total: ${order.Total}</p>
                            </div>
                            <div className="order-products">
                                <h4>Productos:</h4>
                                <div className="products-container">
                                    {order.productos.map(product => (
                                        <div key={product.ProductoID} className="product-card">
                                            <img src={product.Imagen} alt={product.Nombre} className="product-image" />
                                            <p>{product.Nombre} ${product.Precio} x {product.Cantidad}</p>
                                        </div>
                                    ))}
                                </div>
                            </div>
                        </div>
                    ))}
                </div>
            </div>
        </div>
    );
};

export default AdminOrdersPage;
