import React, { useEffect, useState } from 'react';
import axios from 'axios';
import Navbar from '../components/Navbar';
import './AdminPage.css';

const AdminPage = () => {
    const [products, setProducts] = useState([]);
    const [newProduct, setNewProduct] = useState({
        name: '',
        price: '',
        stock: '',
        description: '',
        image: ''
    });
    const [editProduct, setEditProduct] = useState(null);

    useEffect(() => {
        const fetchProducts = async () => {
            try {
                const res = await axios.get('http://localhost:5000/api/products');
                setProducts(res.data);
            } catch (error) {
                console.error('Error fetching products', error);
            }
        };

        fetchProducts();
    }, []);

    const handleAddProduct = async (e) => {
        e.preventDefault();
        try {
            const res = await axios.post('http://localhost:5000/api/products', {
                Nombre: newProduct.name,
                Precio: newProduct.price,
                Stock: newProduct.stock,
                Descripcion: newProduct.description,
                Imagen: newProduct.image
            });
            setProducts([...products, res.data]);
            setNewProduct({ name: '', price: '', stock: '', description: '', image: '' });
        } catch (error) {
            console.error('Error adding product', error);
        }
    };

    const handleEditProduct = async (e) => {
        e.preventDefault();
        try {
            const res = await axios.put(`http://localhost:5000/api/products/${editProduct.ProductoID}`, {
                Nombre: editProduct.Nombre,
                Precio: editProduct.Precio,
                Stock: editProduct.Stock,
                Descripcion: editProduct.Descripcion,
                Imagen: editProduct.Imagen
            });
            setProducts(products.map(product => product.ProductoID === editProduct.ProductoID ? res.data : product));
            setEditProduct(null);
        } catch (error) {
            console.error('Error updating product', error);
        }
    };

    const handleDeleteProduct = async (productId) => {
        try {
            await axios.delete(`http://localhost:5000/api/products/${productId}`);
            setProducts(products.filter(product => product.ProductoID !== productId));
        } catch (error) {
            console.error('Error deleting product', error);
        }
    };

    const [showAddProduct, setShowAddProduct] = useState(false);

    return (
        <div>
            <Navbar />
            <div className="admin-page">
                <div className="sidebar">
                    <h2 className="sidebar-header">Admin Panel</h2>
                    <ul className="sidebar-menu">
                        <li><a href="/products">Productos</a></li>
                        <li><a href="/admin">Admin Panel</a></li>
                        <li><a href="/orders">Order Management</a></li>
                    </ul>
                    <div className="logout-button">
                        <a href="/logout">Cerrar Sesión</a>
                    </div>
                </div>
                <div className="admin-content">
                    <h2>Admin Panel</h2>
                    {!showAddProduct ? (
                        <div className="product-list-container">
                            <h3 className="list-title">Lista de Productos</h3>
                            <ul>
                                {products.map((product) => (
                                    <li key={product.ProductoID}>
                                        <img src={product.Imagen} alt={product.Nombre} className="product-imagen" />
                                        <div className="product-info">
                                            <h4>{product.Nombre}</h4>
                                            <p className="product-price">Precio: ${product.Precio}</p>
                                            <p className="product-stock">Stock: {product.Stock}</p>
                                            <p className="product-description">Descripción: {product.Descripcion}</p>
                                        </div>
                                        <div className="product-actions">
                                            <button onClick={() => handleDeleteProduct(product.ProductoID)}>Eliminar</button>
                                        </div>
                                    </li>
                                ))}
                            </ul>
                            <button className="create-product-button" onClick={() => setShowAddProduct(true)}>CREAR PRODUCTO</button>
                        </div>
                    ) : (
                        <div className="add-product-container">
                            <h3>Añadir Producto</h3>
                            <form className="add-product-form" onSubmit={handleAddProduct}>
                                <input
                                    type="text"
                                    placeholder="Nombre"
                                    value={newProduct.name}
                                    onChange={(e) => setNewProduct({ ...newProduct, name: e.target.value })}
                                />
                                <input
                                    type="number"
                                    placeholder="Precio"
                                    value={newProduct.price}
                                    onChange={(e) => setNewProduct({ ...newProduct, price: e.target.value })}
                                />
                                <input
                                    type="number"
                                    placeholder="Stock"
                                    value={newProduct.stock}
                                    onChange={(e) => setNewProduct({ ...newProduct, stock: e.target.value })}
                                />
                                <textarea
                                    placeholder="Descripción"
                                    value={newProduct.description}
                                    onChange={(e) => setNewProduct({ ...newProduct, description: e.target.value })}
                                ></textarea>
                                <input
                                    type="text"
                                    placeholder="URL de la Imagen"
                                    value={newProduct.image}
                                    onChange={(e) => setNewProduct({ ...newProduct, image: e.target.value })}
                                />
                                <button type="submit">Añadir Producto</button>
                            </form>
                            <button className="view-products-button" onClick={() => setShowAddProduct(false)}>VER LISTA DE PRODUCTOS</button>
                        </div>
                    )}
                </div>
            </div>
        </div>
    );
};

export default AdminPage;
